from random import randintclass Avto():    def __init__(self, znamka, model):        self.znamka = znamka        self.model = modelavto_1 = Avto(znamka="Bmw", model="ActiveHybrid 7")avto_2 = Avto(znamka="Golf", model="Touran")avto_3 = Avto(znamka="Golf", model="Beetle")avto_4 = Avto(znamka="Fiat", model="Panda 4x4")avto_5 = Avto(znamka="Toyota", model="Prius Plug-in Hybrid")avto_6 = Avto(znamka="Subaru", model="Forester")avto_7 = Avto(znamka="Ford", model="Focus")avto_8 = Avto(znamka="Audi", model="R8 Coupe")avtomobili = [avto_1, avto_2, avto_3, avto_4, avto_5, avto_6, avto_7, avto_8]odgovor = "da"while odgovor != "ne":    odgovor = raw_input("Poznas vse znamke avtomobilov? da/ne? ")    if odgovor == "da":        rnd = randint(0, len(avtomobili))        print("Model avtomobila je: " + avtomobili[rnd].model)        uporab_odgovor = raw_input("Kaj pravis, kateri znamka avtomobila pripada: ")        if uporab_odgovor == avtomobili[rnd].znamka:            print("Pravilno si odgovoril/a!")        else:            print("Napacno si odgovorl/a.")            print("Pravilen odgovor se glasi: " + avtomobili[rnd].znamka)    else:        print("Ne zelim vec igrati")        break